import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { ADD_USER, PLUS_LIKE } from "../redux/action/constant";

export default function TestRedux() {
  let { like } = useSelector((state) => state.handleReducer);
  let user = useSelector((state) => state.userReducer.user);
  console.log("user: ", user);
  let dispatch = useDispatch();

  const handlePlusLike = () => {
    dispatch({
      type: PLUS_LIKE,
    });
  };

  const handleAddUser = (lorem1) => {
    dispatch({
      type: ADD_USER,
      user: lorem1,
    });
  };
  return (
    <div className="card text-center mx-auto" style={{ width: 300 }}>
      <img
        className="card-img-top mx-auto"
        src="https://picsum.photos/200/300"
        alt="hinhAnh"
        style={{ width: 150, height: 200 }}
      />
      <div className="card-body">
        <h4 className="card-title">Title</h4>
        <p className="card-text text-justify">
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Sequi
          temporibus cupiditate dolorem dolorum omnis illum vel, fugiat deleniti
          alias magni error magnam nihil nostrum voluptatem provident similique
          earum incidunt quis.
        </p>
        <button
          onClick={() => {
            handlePlusLike();
          }}
          className="btn btn-success"
        >
          + Like
        </button>
        <div>{like}</div>
        <button
          onClick={() => {
            handleAddUser("lorem1");
          }}
          className="btn btn-warning"
        >
          + User
        </button>
        <div>
          {user.map((item, index) => {
            return <div key={index}> {item.userName}</div>;
          })}
        </div>
      </div>
    </div>
  );
}
