import React, { useContext } from "react";
import { useDispatch, useSelector } from "react-redux";
import { ADD_USER } from "../redux/action/constant";

import { context } from "./ContextProvider";

export default function User() {
  console.log("User");

  // let { userReducer } = useContext(context);
  // let user = userReducer[0].user;
  // let dispatchUser = userReducer[1];
  let [userArr, dispatchUser] = useContext(context).userReducer;
  let user = userArr.user;

  const handleAddUser = (lorem) => {
    dispatchUser({
      type: ADD_USER,
      user: lorem,
    });
  };

  return (
    <div>
      <button
        onClick={() => {
          handleAddUser({ userName: "lorem1" });
        }}
        className="btn btn-warning"
      >
        + User
      </button>
      <div>
        {user.map((item, index) => {
          return <div key={index}> {item.userName}</div>;
        })}
      </div>
    </div>
  );
}

